$(document).ready(function () {
    tablaInf = $("#tablaInf").DataTable({
        "order": [[0, "desc"]],
        "columnDefs": [{
            "targets": -1,
            "data": null,
            "defaultContent": '<div class="text-center "><div class="btn-group"><button class="btn btn-info  btnEditar">Editar</button> <button class="btn btn-danger btnBorrar">Eliminar</button></div></div>'
        }],

        //  cambio de idioma
        "language": {
            "lengthMenu": "Mostar _MENU_ Registros",
            "zeroRecords": "No se encontraron resultados",
            "info": "Mostrando registros del _START_ al _END_ de un total de _TOTAL_ registros",
            "infoEmpty": "Mostrando registros del 0 al 0 de un total de _MAX_ registros",
            "infoFiltered": "(Filtrando de un Total de _MAX_ registros)",
            "sSearch": "Buscar: ",
            "oPaginate": {
                "sFrist": "Primero",
                "sLast": "Ultimo",
                "sNext": "Siguiente",
                "sPrevious": "Anterior"
            },
            "sProcessing": "Procesando...",
        }
    });

    $("#btnNuevo").click(function () {
        $("#formDatos").trigger("reset");
        $(".modal-header").css("background-color", "#28a745");
        $(".modal-title").text("Agregar Infectados");
        $("#modalCrud").modal("show");
        opcion = 1;
    });

    $(document).on("click", ".btnEditar", function () {
        fila = $(this).closest("tr");
        id = parseInt(fila.find('td:eq(0)').text());
        fecha = fila.find('td:eq(1)').text();
        infectados = fila.find('td:eq(2)').text();

        $("#fecha").val(fecha);
        $("#infectados").val(infectados);
        opcion = 2;
        $(".modal-header").css("background-color", "#007bff");
        $(".modal-title").text("Editar Infectados");
        $("#modalCrud").modal("show");

    });

    $(document).on("click", ".btnBorrar", function () {
        opcion = 3;
        fila = $(this);
        id = parseInt($(this).closest("tr").find('td:eq(0)').text());
        var respuesta = confirm("Â¿Estas Seguro de borrar el id=" + id + "?");
        if (respuesta) {
            $.ajax({
                url: "abm.php",
                type: "POST",
                datatype: "json", 
                data: { opcion: opcion, id: id },
                success: function () {
                    tb = $("#tablaInf").DataTable();
                    tb.row(fila.parents('tr')).remove().draw();
                }
            });
        }
    });

    $("#formDatos").submit(function (e) {
        // evita que se recarge la pagina
        e.preventDefault();
        fec = $("#fecha").val();
        inf = $("#infectados").val();
       
       
        $.ajax({
            url: "abm.php",
            type: "POST",
            datatype: "json",
            data: { fecha: fec, infectados: inf, opcion: opcion },
            success: function (data) {
                 alert("holaa"+ data);
                var datos = JSON.parse(data);
                fecha = datos[0].fecha;
                infec = datos[0].infectados;
                total = datos[0].totalDia;
                factor = datos[0].factor;
                promedio = datos[0].promerioFactor;
                posibles = datos[0].posible;
                 id=datos[0].id;
                // alert("aca"+datos[0].fecha);

                
                if (opcion == 1) {
                    tb = $("#tablaInf").DataTable();
                    //  id='<td hidden>'+id+'</td>';
                    tb.row.add([fecha, infec,total,factor,promedio]).draw();
                    alert("Posibles Casos"+posible);

                    document.getElementById('posiblecaso').innerHTML=posibles;
                }else{
                    id='<td hidden>'+id+'</td>';
                    tb.row(fila).data([id,fecha, infec, "", "", "",""]).draw()
                }
            }
        });
        $("#modalCrud").modal("hide");
    });


});

function fechaMa(){
    const fechaDeAyer = () => {
        let hoy = new Date();
        let DIA_EN_MILISEGUNDOS = 24 * 60 * 60 * 1000;
        let ayer = new Date(hoy.getTime() - DIA_EN_MILISEGUNDOS);
        return ayer;
    }
    document.getElementById('fecM').innerHTML=fechaDeAyer();
    $("#fecM").html(fechaDeAyer());
    $("#fecM").innerHTML='Putooo';
    document.querySelector('#fecM').innerText="hola";
}